wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(15118535555704162520)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'DE.IT-TWINS.CM-DISPLAY'
,p_display_name=>'CodeMirror Display'
,p_supported_ui_types=>'DESKTOP'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','DE.IT-TWINS.CM-DISPLAY'),'')
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'FUNCTION render ( p_item IN apex_plugin.t_page_item',
'                , p_plugin IN apex_plugin.t_plugin',
'                , p_value IN VARCHAR2',
'                , p_is_readonly IN BOOLEAN',
'                , p_is_printer_friendly IN BOOLEAN',
'                )',
'  RETURN apex_plugin.t_page_item_render_result',
'AS',
'  c_onload_code CONSTANT VARCHAR2(2000) := ''CodeMirror.runMode(document.getElementById("code_#ITEM#").value, "#CODE_TYPE#", document.getElementById("output_#ITEM#"))'';',
'  l_code_type apex_application_page_items.attribute_01%TYPE := p_item.attribute_01;',
'  l_code_js_file VARCHAR2(50);',
'  l_theme apex_application_page_items.attribute_02%TYPE := p_item.attribute_02;',
'  l_result apex_plugin.t_page_item_render_result;',
'  FUNCTION get_onload_js',
'    RETURN VARCHAR2',
'  AS',
'  BEGIN',
'    RETURN REPLACE( REPLACE( c_onload_code, ''#ITEM#'', p_item.NAME ), ''#CODE_TYPE#'', l_code_type);',
'  END get_onload_js;',
'BEGIN',
'  apex_plugin_util.debug_page_item ( p_plugin => p_plugin',
'                                   , p_page_item => p_item',
'                                   , p_value => p_value',
'                                   , p_is_readonly => p_is_readonly',
'                                   , p_is_printer_friendly => p_is_printer_friendly',
'                                   );',
'  l_code_js_file := CASE l_code_type',
'                      WHEN ''text/x-plsql'' THEN ''sql''',
'                      WHEN ''text/javascript'' THEN ''javascript''',
'                      WHEN ''application/x-json'' THEN ''javascript''',
'                      ELSE NULL',
'                    END;',
'',
'  apex_javascript.add_library( p_name => ''codemirror''',
'                             , p_directory => p_plugin.file_prefix',
'                             );',
'  ',
'  apex_javascript.add_library( p_name => ''runmode''',
'                             , p_directory => p_plugin.file_prefix',
'                             );',
'',
'  apex_javascript.add_library( p_name => l_code_js_file',
'                             , p_directory => p_plugin.file_prefix',
'                             );',
'',
'  apex_css.add_file( p_name => ''codemirror''',
'                   , p_directory => p_plugin.file_prefix',
'                   );',
'  IF l_theme != ''default'' THEN',
'    apex_css.add_file( p_name => l_theme',
'                     , p_directory => p_plugin.file_prefix',
'                     );',
'  END IF;',
'    ',
'  apex_javascript.add_onload_code( p_code => get_onload_js );',
'  ',
'  sys.htp.p(''<textarea id="code_'' || p_item.name || ''" style="display: none;">'' || p_value || ''</textarea>'');',
'  sys.htp.p(''<pre id="output_'' || p_item.NAME || ''" class="cm-s-'' || l_theme || ''"></pre>'');',
'  ',
'  RETURN l_result;',
'END;'))
,p_render_function=>'render'
,p_standard_attributes=>'VISIBLE:SOURCE'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'0.2'
,p_files_version=>7
);
